const textListReducer = (state = [], action) => {
    switch (action.type) {
        case 'NUEVO_TEXTO':
            return [action.text, ...state]
        case 'SIN_TEXTO':
            return state.unshift(action.error)
        default:
            return state
    }
}

export default textListReducer
